import React from "react";
import { NavLink } from "react-router-dom";
import classes from './Dialogs.module.css';
// import DialogsItem from './DialogItem/DialogsItem';
import Message from './Message/Message';
import MessageAva from './Message/MessageAva/MessageAva'

const Dialogs = (props) => {

    let MessageAvatar = props.dialogsData.map(mess => < MessageAva name={mess.name} id={mess.id} />)

    // let dialogElements = props.dialogsData
    //     .map(d => < DialogsItem name={d.name} id={d.id} />)
    let newPostElement = React.createRef();

    let addPost = () => {
        let text = newPostElement.current.value;
        alert(text);
    }

    let messageElement = props.messages.map(m => < Message message={m.message} />)
    // let onPostChenge = () => {
    //     let text = newPostElement.current.value;
    //     props.newMessageText(text)
    // }

    return (
        <div className={classes.dialogs}>
            {/* <div className={classes.dialogsItems}>
                {dialogElements}
            </div> */}
            <div className={classes.messages}>
                {MessageAvatar}
            </div>
            <div className={classes.messages}>
                {messageElement}
                <div>
                    <textarea  ref={newPostElement} className={classes.dialogs}></textarea>
                </div>
                <div>
                    <button onClick={addPost}>Add post</button>
                </div>
            </div>

        </div>


    )
}
export default Dialogs
