import React from "react";
import classes from './Dialogs.module.css';
import Message from './Message/Message';
import MessageAva from './Message/MessageAva/MessageAva'
import {addMessageActionCreator, updateNewMessageActionCreator} from '../../redux/state'

const Dialogs = (props) => {

    let MessageAvatar = props.dialogsData.map(mess => < MessageAva name={mess.name} id={mess.id} />)

    let newPostElement = React.createRef();

    let messageElement = props.messages.map(m => < Message message={m.message} />)

    let addMessage = () => {

        props.dispatch(addMessageActionCreator());

    }

    let onMessageChenge = () => {
        let text = newPostElement.current.value;
        let action = (updateNewMessageActionCreator (text));
        props.dispatch(action);
    }

    return (
        <div className={classes.dialogs}>

            <div className={classes.messages}>
                {MessageAvatar}
            </div>
            <div className={classes.messages}>
                {messageElement}
                <div>
                    <textarea onChange={onMessageChenge} ref={newPostElement} className={classes.dialogs} value={props.newMessageText} />
                </div>
                <div>
                    <button onClick={addMessage}>Add post</button>
                </div>
            </div>

        </div>


    )
}
export default Dialogs
