                { id: 3, message: 'you are good' },
                { id: 4, message: 'yo' },
                { id: 5, message: 'you' }
            ],
            newMessageText: 'it-proframing'
        },

        messagesPege: {
            dialogsData: [
                { id: 1, name: 'Aleksey' },
                { id: 2, name: 'Dmitiy' },
                { id: 3, name: 'Grigorii' },
                { id: 4, name: 'Eugeny' },
                { id: 5, name: 'Artem' }
            ],
            postData: [
                { id: 1, post: 'Hi, how are you?', likeCount: 5 },
                { id: 2, post: "It's my first post", likeCount: 8 },
                { id: 3, post: 'you are good', likeCount: 8 },
                { id: 4, post: 'yo', likeCount: 8 },
                { id: 5, post: 'you', likeCount: 8 }
            ],
            newPostText: 'it-proframing'
        }
    },
    _callSubscriber () {
        console.log('State chenged')
    },

    getState() {
        return this._state
    },   
    subscribe(observer) {
        this._callSubscriber = observer;

    },

     addPost () {
// debugger;
        let newPost = {
            id: 5,
            post: this._state.messagesPege.newPostText,
            likeCount: 0
        };

        this._state.messagesPege.postData.push(newPost);
        this._state.messagesPege.newPostText = '';
        this._callSubscriber( this._state)
    },
    updateNewPostText (NewTex) {
        this._state.messagesPege.newPostText = NewTex;
        this._callSubscriber( this._state)
    },
    addMessage() {

        let newMessage = {
            id: 5,
            message:  this._state.profile.newMessageText
        }
        this._state.profile.messages.push(newMessage);

        this._callSubscriber( this._state)
    },
     updateNewMessage(newMessages) {
        this._state.profile.newMessageText = newMessages;
        this._callSubscriber( this._state)
    },
     dispatch(action) { {type: 'ADD-POST'}
     if (action.type === 'ADD-POST') {
        let newPost = {
            id: 5,
            post: this._state.messagesPege.newPostText,
            likeCount: 0
        };

        this._state.messagesPege.postData.push(newPost);
        this._state.messagesPege.newPostText = '';
        this._callSubscriber( this._state)
     } else if (action.type === 'UPDATE-NEW-POST-TEXT') {
        this._state.messagesPege.newPostText = NewTex;
        this._callSubscriber( this._state)
     } else if (action.type === 'ADD-MESSAGE') {
        let newMessage = {
            id: 5,
            message:  this._state.profile.newMessageText
        }
        this._state.profile.messages.push(newMessage);

        this._callSubscriber( this._state)
     } else if (action.tipe === 'UPDATE-NEW-MESSAGE') {
        this._state.profile.newMessageText = newMessages;
        this._callSubscriber( this._state)
     }

     }
}



export default store;
window.store = store;
