import React from 'react';
import classes from './MyPosts.module.css';
import Post from './Post/Post'

const MyPosts = (props) => {
  // let postData = [
  //   { id: 1, post: 'Hi, how are you?', likeCount: 5 },
  //   { id: 2, post: "It's my first post", likeCount: 8 },
  //   { id: 3, post: 'you are good' },
  //   { id: 4, message: 'yo' },
  //   { id: 5, message: 'you' }
  // ]
  let postsElement =
 props.postData.map ( p => <Post message={p.post} likeCount={p.likeCount} /> )
 let newPostElement = React.createRef
 let addPost = () => {
   let text = newPostElement.current.value;
   alert(text)
 }
  return (
    <div className={classes.postBlock}>
      <h3>My posts</h3>
      <div>
        <div>
          <textarea ref={newPostElement}></textarea>
        </div>
        <div>
          <button onClick={ addPost }>Add post</button>
        </div>
      </div>

      <div className={classes.posts}>
        {postsElement}
        {/* <Post message={postData[0].post} likeCount={postData[0].likeCount} />
        <Post message={postData[1].post} likeCount={postData[1].likeCount} /> */}
        {/* <Post />
        <Post />
        <Post /> */}

      </div>
    </div>
  )

}
export default MyPosts
