import rerenderEntireTree from '../render.js'
let state = {
    profile: {

        messages: [
            { id: 1, message: 'Hi' },
            { id: 2, message: 'Hi' },
            { id: 3, message: 'you are good' },
            { id: 4, message: 'yo' },
            { id: 5, message: 'you' }
        ],
        newMessageText: 'it-proframing'
    },

    messagesPege: {
        dialogsData: [
            { id: 1, name: 'Aleksey' },
            { id: 2, name: 'Dmitiy' },
            { id: 3, name: 'Grigorii' },
            { id: 4, name: 'Eugeny' },
            { id: 5, name: 'Artem' }
        ],
        postData: [
            { id: 1, post: 'Hi, how are you?', likeCount: 5 },
            { id: 2, post: "It's my first post", likeCount: 8 },
            { id: 3, post: 'you are good', likeCount: 8 },
            { id: 4, post: 'yo', likeCount: 8 },
            { id: 5, post: 'you', likeCount: 8 }
        ],
        newPostText: 'it-proframing'
    }
}
export let addPost = () => {


    let newPost = {
        id: 5,
        post: state.messagesPege.newPostText,
        likeCount: 0
    };

    state.messagesPege.postData.push(newPost);
    state.messagesPege.newPostText = '';
    rerenderEntireTree(state)
}

export let updateNewPostText = (NewTex) => {
    state.messagesPege.newPostText = NewTex;
    rerenderEntireTree(state)
}
export let addMessage = () => {

    let newMessage = {
        id: 5,
        message: state.profile.newMessageText
    }
    state.profile.messages.push(newMessage);
    // state.messages.newMessageText = '';
    rerenderEntireTree(state)
}
export let updateNewMessage = (newMessages) => {
    state.profile.newMessageText = newMessages;
    rerenderEntireTree(state)
}

export default state
